/* eslint-disable jsx-a11y/img-redundant-alt */
/* eslint-disable jsx-a11y/alt-text */
import React from 'react'; 
import {Link} from 'react-router-dom';
import '../../index.css';
import styled from 'styled-components';
// Material UI
import { makeStyles } from '@material-ui/core/styles';
import {Grid, Card} from '@material-ui/core';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import Typography from '@material-ui/core/Typography';
import MenuList from '@material-ui/core/MenuList';
import MenuItem from '@material-ui/core/MenuItem';
// icons
import {FaBasketballBall, FaSkiing, FaChild, FaFacebook, FaPaperPlane} from "react-icons/fa";
import {GiJumpAcross, GiSawedOffShotgun, GiTeamDowngrade} from "react-icons/gi";
import {BiSwim} from "react-icons/bi";

const useStyles = makeStyles({
    title: {
     fontFamily:'ubuntu',
    },
    img: {
        width: 500,
        // marginLeft: -45,
        borderRadius: 20,
    },
    navleft: {
        width: 160,
        backgroundColor: 'grey',
        color: 'black',
        borderRadius: 20,
    },
    linetwo: {
        marginTop: 20,
    },
    linethree: {
        marginTop:20,
    },
    cardtxt: {
        padding: 20,
        backgroundColor: 'grey',
        borderTopRightRadius: 60,
        borderBottomLeftRadius: 60,
    },
    cardtxtTwo: {
        padding: 20,
        backgroundColor: 'grey',
        borderTopRightRadius: 60,
        borderBottomLeftRadius: 60,
        paddingTop: 60,
        paddingBottom:60,
    },
    bold: {
        fontWeight: 700,
    },
    underline: {
        textDecorationLine: 'underline',
    },
    paraimg: {
        width: 500,
        marginTop: 70,
    },
    peopleimg: {
        width: 400,
        marginLeft: 30,
        marginTop:45,
    }

});

const StyledBody = styled.div`
  background: url('http://web3canvas.com/wp-content/uploads/2014/02/littlevisuals-high-quality-hd-banner-images-stock-photos-free-laptop-topview-download.jpg');
  background-size: cover;
  background-color: red;
//   position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
//   width: 100%;
  height: 200%;
`;
export default function Home(){
    const classes = useStyles();



return (
<StyledBody>
<div className="homePage">
<Grid container xs={12} direction="row" justify="center" alignItems="center">
        <h1 className={classes.title}>Bienvenue à l'ASHHV</h1>
    </Grid>
        <Grid sx={12} container direction="row"  alignItems="center" justify="space-between">
            <Grid xs={2}alignItems="flex-start">
            <MenuList className={classes.navleft}>
            <MenuItem className={classes.listline}>
                <Link to="/basket-fauteuil">
                <Typography variant="inherit" className={classes.align}>Basket</Typography>
                </Link>
